import api from './services/api';

import './App.css';

function App() {
  const [names, setNames] = useState([]);

  useEffect(()=>{
    api.get('/').then(response => {
      setNames(response.data)
    })
  }, [names]);


  var groups = names.reduce((groupArray, item, index) => {
    var chunkIndex = Math.floor(names.length/4);

    if(!groupArray[chunkIndex]){
      groupArray[chunkIndex] = [];
    }

    groupArray[chunkIndex].push(item);

    return groupArray
