import React, { useEffect, useState } from 'react';
import api from './services/api';

import './App.css';

/* function chunkArray(myArray, chunk_size){
  var index = 0;
  var arrayLength = myArray.length;
  var tempArray = [];
  var myChunk;

  for (index; index < arrayLength; index += chunk_size) {
      myChunk = myArray.slice(index, index+chunk_size);
      // Do something if you want with the group
      tempArray.push(myChunk);
  }

  return tempArray;
} */

function App() {
  const [names, setNames] = useState([]);


  var chunk_size = Math.floor(names.length/4);

  useEffect(()=>{
    api.get('/').then(response => {
      setNames(response.data);
    });

  }, [names]);

  var index = 0;
  var arrayLength = names.length;
  var myChunk;

  for (index; index < arrayLength; index += chunk_size) {
      myChunk = names.slice(index, index+chunk_size);

      setNames(myChunk);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Grupos</h1>
      </header>

      <ul>
        <p>Grupo 1</p>
        <p>{names}</p>
        {/* {group1.map(name => (
          <li key={name.name}>
          <p>{name.name}</p>
        </li>
        ))
        } */}

      </ul>

    </div>
  );

}

export default App;
